package com.rittmanmead.mtndevops

import oracle.odi.core.OdiInstance
import oracle.odi.core.config.MasterRepositoryDbInfo
import oracle.odi.core.config.OdiInstanceConfig
import oracle.odi.core.config.PoolingAttributes
import oracle.odi.core.config.WorkRepositoryDbInfo
import oracle.odi.core.persistence.IOdiEntityManager
import oracle.odi.core.persistence.transaction.ITransactionManager
import oracle.odi.core.persistence.transaction.ITransactionStatus
import oracle.odi.core.persistence.transaction.support.DefaultTransactionDefinition
import oracle.odi.core.security.Authentication
import oracle.odi.domain.project.OdiInterface
import oracle.odi.domain.project.OdiPackage
import oracle.odi.domain.project.OdiUserProcedure
import oracle.odi.domain.project.OdiVariable
import oracle.odi.domain.runtime.scenario.OdiScenario
import oracle.odi.domain.runtime.scenario.finder.IOdiScenarioFinder
import oracle.odi.generation.support.OdiScenarioGeneratorImpl
import oracle.odi.impexp.EncodingOptions
import oracle.odi.impexp.support.ExportServiceImpl
import oracle.odi.logging.LoggerUtils
import org.eclipse.jgit.api.Git
import org.eclipse.jgit.api.PullCommand
import org.eclipse.jgit.api.PushCommand
import org.eclipse.jgit.internal.storage.file.FileRepository
import org.eclipse.jgit.transport.UsernamePasswordCredentialsProvider

import java.util.logging.Level
import java.util.logging.Logger

class odi2git {

    static main(args) {
        //--------------------------------------------------------------
        // Configuration START

        //def configDir = "d:\\ODIRMT\\config\\"
        def configDir = "#RELEASE_MANAGEMENT.LOCAL_CONFIG_DIR"

        //def release_tag = '1.0'
        def release_tag = "#RELEASE_MANAGEMENT.RELEASE_TAG"

        //def commit_message = release_tag + ' added/updated'
        def commit_message = "#RELEASE_MANAGEMENT.COMMIT_MESSAGE"

        //def remove_untagged_files_from_git = 1
        def remove_untagged_files_from_git = "#RELEASE_MANAGEMENT.REMOVE_UNTAGGED"

        //def regenerate_scenarios = 1
        def regenerate_scenarios = "#RELEASE_MANAGEMENT.REGENERATE_SCENARIOS"

        // Configuration END
        //--------------------------------------------------------------

        def git

        LoggerUtils.setLogLevel(Logger.global, Level.OFF)

        // Load git properties
        Properties gitProps = new Properties()
        File gitPropFile = new File(configDir + 'git.properties')
        gitProps.load(gitPropFile.newDataInputStream())

        // Load environment properties
        Properties envProps = new Properties()
        File envPropFile = new File(configDir + 'DEV' + '.properties')
        envProps.load(envPropFile.newDataInputStream())

        //
        // Clone/Pull from remote repository to local
        //
        if ( new File(gitProps.localGitDir + '\\.git').exists()) {
            println "pulling"
            def localRepo = new FileRepository(gitProps.localGitDir + "\\.git");
            git = new Git(localRepo)
            PullCommand pullCmd = git.pull().setCredentialsProvider( new UsernamePasswordCredentialsProvider(gitProps.username,gitProps.password))
            pullCmd.call()
        } else {
            println "cloning"
            git = Git.cloneRepository()
                .setCredentialsProvider( new UsernamePasswordCredentialsProvider(gitProps.username,gitProps.password))
                .setURI(gitProps.remoteGitUrl)
                .setDirectory(new File(gitProps.localGitDir))
                .call()
        }

        def getReleaseTagFileMetadata = { releaseTag ->
            def files = []
            def getDesignObjectFiles
            getDesignObjectFiles = { File dir ->
                for (File file : dir.listFiles()) {
                    if (file.isDirectory()) {
                        getDesignObjectFiles(file)
                    } else {
                        files << [file: file, filename :file.getAbsoluteFile().toString(), lastModified: file.lastModified() ]
                    }
                }
            }
            def getScenariosFiles
            getScenariosFiles = { File dir ->
                for (File file : dir.listFiles()) {
                    if (file.isDirectory()) {
                        getScenariosFiles(file)
                    } else {
                        files << [file:file, filename :file.getAbsoluteFile().toString(), lastModified: file.lastModified() ]
                    }
                }
            }
            getDesignObjectFiles( new File(gitProps.localGitDir + release_tag + "\\design_objects\\"))
            getScenariosFiles( new File(gitProps.localGitDir + release_tag + "\\scenarios\\"))
            return files
        }

        def beforeFiles = getReleaseTagFileMetadata(release_tag)

        //
        // Export scenarios for the given release
        //
        MasterRepositoryDbInfo masterInfo = new MasterRepositoryDbInfo(envProps.jdbc_url, envProps.jdbc_driver, envProps.jdbc_username, envProps.jdbc_password.toCharArray(), new PoolingAttributes())
        WorkRepositoryDbInfo workInfo = new WorkRepositoryDbInfo(envProps.odi_work_repo, new PoolingAttributes())
        OdiInstance odiInstance = OdiInstance.createInstance(new OdiInstanceConfig(masterInfo, workInfo))
        Authentication auth = odiInstance.getSecurityManager().createAuthentication(envProps.odi_studio_user, envProps.odi_studio_password.toCharArray())
        odiInstance.getSecurityManager().setCurrentThreadAuthentication(auth)
        DefaultTransactionDefinition txnDef = new DefaultTransactionDefinition()
        ITransactionManager tm = odiInstance.getTransactionManager()
        IOdiEntityManager tme = odiInstance.getTransactionalEntityManager()
        ITransactionStatus trans = tm.getTransaction(txnDef)
        EncodingOptions EncdOption = new EncodingOptions()
        ExportServiceImpl export = new ExportServiceImpl(odiInstance)
        OdiScenarioGeneratorImpl scenGenerator = new OdiScenarioGeneratorImpl(odiInstance)

        // Get Packages for given release tag
        tme.getFinder(OdiPackage.class).findAll().each() { OdiPackage pack ->
            if (pack.getDescription() != null) {
                def tokens = pack.getDescription().split('\n')[0].split('^RELEASE_TAG:')
                if (tokens.length > 1) {
                    def tag = tokens[1].trim()
                    if (tag == release_tag) {
                        // Export package
                        export.exportToXml(pack, gitProps.localGitDir + tag + "\\design_objects\\packages", true, true, EncdOption)
                        // find the version 001 scenario for this Package
                        ((IOdiScenarioFinder)tme.getFinder(OdiScenario.class)).findBySourcePackage(pack.getPackageId()).each {OdiScenario scen ->
                            if ( scen.getVersion() == '001') {
                                if ( regenerate_scenarios > 0 ) {
                                    scenGenerator.regenerateScenario(scen)
                                }
                                // Export package scenario
                                export.exportToXml(scen, gitProps.localGitDir + tag + "\\scenarios", true, true, EncdOption)
                            }
                        }
                    }
                }
            }
        }

        // Get Procedures for given release tag
        tme.getFinder(OdiUserProcedure.class).findAll().each() { OdiUserProcedure proc ->
            if (proc.getDescription() != null) {
                def tokens = proc.getDescription().split('\n')[0].split('^RELEASE_TAG:')
                if (tokens.length > 1) {
                    def tag = tokens[1].trim()
                    if (tag == release_tag) {
                        // Export procedure
                        export.exportToXml(proc, gitProps.localGitDir + tag + "\\design_objects\\procedures", true, true, EncdOption)
                        // find the version 001 scenario for this Package
                        ((IOdiScenarioFinder)tme.getFinder(OdiScenario.class)).findBySourceUserProcedure(proc.getProcedureId()).each {OdiScenario scen ->
                            if ( scen.getVersion() == '001') {
                                if ( regenerate_scenarios > 0 ) {
                                    scenGenerator.regenerateScenario(scen)
                                }
                                // Export procedure scenario
                                export.exportToXml(scen, gitProps.localGitDir + tag + "\\scenarios", true, true, EncdOption)
                            }
                        }
                    }
                }
            }
        }

        // Get Interfaces for given release tag
        tme.getFinder(OdiInterface.class).findAll().each() { OdiInterface intf ->
            if (intf.getDescription() != null) {
                def tokens = intf.getDescription().split('\n')[0].split('^RELEASE_TAG:')
                if (tokens.length > 1) {
                    def tag = tokens[1].trim()
                    if (tag == release_tag) {
                        // Export interface
                        export.exportToXml(intf, gitProps.localGitDir + tag + "\\design_objects\\interfaces", true, true, EncdOption)
                        // find the version 001 scenario for this Package
                        ((IOdiScenarioFinder)tme.getFinder(OdiScenario.class)).findBySourceInterface(intf.getInterfaceId()).each {OdiScenario scen ->
                            if ( scen.getVersion() == '001') {
                                if ( regenerate_scenarios > 0 ) {
                                    scenGenerator.regenerateScenario(scen)
                                }
                                // Export interface scenario
                                export.exportToXml(scen, gitProps.localGitDir + tag + "\\scenarios", true, true, EncdOption)
                            }
                        }
                    }
                }
            }
        }

        // Get Variables for given release tag
        tme.getFinder(OdiVariable.class).findAll().each() { OdiVariable var ->
            if (var.getDescription() != null) {
                def tokens = var.getDescription().split('\n')[0].split('^RELEASE_TAG:')
                if (tokens.length > 1) {
                    def tag = tokens[1].trim()
                    if (tag == release_tag) {
                        // Export variable
                        export.exportToXml(var, gitProps.localGitDir + tag + "\\design_objects\\variables", true, true, EncdOption)
                        // find the version 001 scenario for this Package
                        ((IOdiScenarioFinder)tme.getFinder(OdiScenario.class)).findBySourceVariable(var.getVariableId()).each {OdiScenario scen ->
                            if ( scen.getVersion() == '001') {
                                if ( regenerate_scenarios > 0 ) {
                                    scenGenerator.regenerateScenario(scen)
                                }
                                // Export variable scenario
                                export.exportToXml(scen, gitProps.localGitDir + tag + "\\scenarios", true, true, EncdOption)
                            }
                        }
                    }
                }
            }
        }
        tm.commit(trans)
        odiInstance.close()

        def afterFiles = getReleaseTagFileMetadata(release_tag)

        if( remove_untagged_files_from_git > 0 ) {
            beforeFiles.each() { beforeFile ->
                afterFiles.each() { afterFile ->
                    if (beforeFile.equals(afterFile)) {
                        def filename = release_tag + '/' + beforeFile.filename.toString().split('\\\\' + release_tag + '\\\\')[1].replace('\\', '/')
                        git.rm().addFilepattern(filename).call()
                        println "removing " + filename
                    }
                }
            }
        }


        git.add().addFilepattern(release_tag).call()

        try {
            git.commit().setMessage(commit_message).call()
        } catch (Exception e) {
            println e
        }

        PushCommand pushCmd = git.push().setCredentialsProvider( new UsernamePasswordCredentialsProvider(gitProps.username,gitProps.password))
        pushCmd.call()
        git.close()
    }
}

package com.rittmanmead.mtndevops

import oracle.odi.core.OdiInstance
import oracle.odi.core.config.MasterRepositoryDbInfo
import oracle.odi.core.config.OdiInstanceConfig
import oracle.odi.core.config.PoolingAttributes
import oracle.odi.core.config.WorkRepositoryDbInfo
import oracle.odi.core.persistence.IOdiEntityManager
import oracle.odi.core.persistence.transaction.ITransactionManager
import oracle.odi.core.persistence.transaction.ITransactionStatus
import oracle.odi.core.persistence.transaction.support.DefaultTransactionDefinition
import oracle.odi.core.security.Authentication
import oracle.odi.impexp.EncodingOptions
import oracle.odi.impexp.support.ImportServiceImpl
import oracle.odi.logging.LoggerUtils
import org.eclipse.jgit.api.Git
import org.eclipse.jgit.api.PullCommand
import org.eclipse.jgit.internal.storage.file.FileRepository
import org.eclipse.jgit.transport.UsernamePasswordCredentialsProvider

import java.util.logging.Level
import java.util.logging.Logger

class git2odi {

    static main(args) {

        //--------------------------------------------------------------
        // Configuration START

        //def configDir = "d:\\ODIRMT\\config\\"
        def configDir = "#RELEASE_MANAGEMENT.LOCAL_CONFIG_DIR"

        //def targetEnv = 'DEV'
        def targetEnv = "#RELEASE_MANAGEMENT.ENVIRONMENT"

        //def release_tag = '1.0'
        def release_tag = "#RELEASE_MANAGEMENT.RELEASE_TAG"

        //def import_design_objects = 1
        def import_design_objects = "#RELEASE_MANAGEMENT.IMPORT_DESIGN_OBJECTS"

        // Configuration END
        //--------------------------------------------------------------

        def git
        LoggerUtils.setLogLevel(Logger.global, Level.OFF)

        // Load git properties
        Properties gitProps = new Properties()
        File gitPropFile = new File(configDir + 'git.properties')
        gitProps.load(gitPropFile.newDataInputStream())

        // Load environment properties
        Properties envProps = new Properties()
        File envPropFile = new File(configDir + targetEnv + '.properties')
        envProps.load(envPropFile.newDataInputStream())

        //
        // Clone/Pull from remote repository to local
        //
        if ( new File(gitProps.localGitDir + '\\.git').exists()) {
            println "pulling"
            def localRepo = new FileRepository(gitProps.localGitDir + "\\.git");
            git = new Git(localRepo)
            PullCommand pullCmd = git.pull().setCredentialsProvider( new UsernamePasswordCredentialsProvider(gitProps.username,gitProps.password))
            pullCmd.call()
        } else {
            println "cloning"
            git = Git.cloneRepository()
                .setURI(gitProps.remoteGitUrl)
                .setCredentialsProvider( new UsernamePasswordCredentialsProvider(gitProps.username,gitProps.password))
                .setDirectory(new File(gitProps.localGitDir))
                .call()
        }

        //
        // Import scenarios for the given release
        //
        MasterRepositoryDbInfo masterInfo = new MasterRepositoryDbInfo(envProps.jdbc_url, envProps.jdbc_driver, envProps.jdbc_username, envProps.jdbc_password.toCharArray(), new PoolingAttributes())
        WorkRepositoryDbInfo workInfo = new WorkRepositoryDbInfo(envProps.odi_work_repo, new PoolingAttributes())
        OdiInstance odiInstance = OdiInstance.createInstance(new OdiInstanceConfig(masterInfo, workInfo))
        Authentication auth = odiInstance.getSecurityManager().createAuthentication(envProps.odi_studio_user, envProps.odi_studio_password.toCharArray())
        odiInstance.getSecurityManager().setCurrentThreadAuthentication(auth)
        DefaultTransactionDefinition txnDef = new DefaultTransactionDefinition()
        ITransactionManager tm = odiInstance.getTransactionManager()
        IOdiEntityManager tme = odiInstance.getTransactionalEntityManager()
        ITransactionStatus trans = tm.getTransaction(txnDef)
        EncodingOptions EncdOption = new EncodingOptions()
        ImportServiceImpl imp = new ImportServiceImpl(odiInstance)

        if ( odiInstance.workRepository.type.toString() == 'DESIGN' && import_design_objects > 0 ) {
            // Import design objects
            def getDesignObjectFiles
            getDesignObjectFiles = { File dir ->
                for (File file : dir.listFiles()) {
                    if (file.isDirectory()) {
                        getDesignObjectFiles(file)
                    } else {
                        try {
                            imp.importObjectFromXml(4, file.getAbsolutePath(), true)
                        } catch (e) {
                            println e
                        }

                    }
                }
            }
            getDesignObjectFiles(new File(gitProps.localGitDir + release_tag + "\\design_objects\\"))
        }

        // Import Scenarios
        def currentDir = new File(gitProps.localGitDir +  release_tag + '\\scenarios')
        try {
            currentDir.eachFile { File file ->
                imp.importObjectFromXml(4, file.getAbsolutePath(), true)
            }
        } catch ( FileNotFoundException e) {
            println "Release tag does not exist in VCS"
        }

        tm.commit(trans)
        odiInstance.close()
        git.close()
    }
}

